var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.messages = new Datastore({
  filename: "storage/ircMessages.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.messages.loadDatabase();
db.messages.ready = ready.promise;
module.exports = db.messages;
