var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.users = new Datastore({
  filename: "storage/Users.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.users.loadDatabase();
db.users.ready = ready.promise;
module.exports = db.users;
