var Datastore = require("nedb");
var spromise  = require("spromise");

var ready = spromise.defer();
var db = {};

db.connections = new Datastore({
  filename: "storage/ircConnections.db",
  autoload: true,
  onload: function(err) {
    if ( err ) {
      ready.reject(err);
    }
    else {
      ready.resolve(db);
    }
  }
});

db.connections.loadDatabase();
db.connections.ready = ready.promise;
module.exports = db.connections;
